(function(){KD.utils.Date={getLocale:function(){return KD.utils.Util.getLocale();},getLocaleDateString:function(a){var b=new Date(Date.parse(a));return YAHOO.util.Date.format(b,{format:"%x"},this.getLocale());},getLocaleDatetimeString:function(b){var a=this.getLocale(),c=new Date(Date.parse(b));a=a.substring(0,2)+a.substring(2,5).toUpperCase();return YAHOO.util.Date.format(c,{format:"%x"},a)+" "+YAHOO.util.Date.format(c,{format:"%X"},a);}};})();